import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const AddStudents = () => {
  const [image, setImage] = useState(null);

  const [imageURL, setImageURL] = useState("");
  const [isLoading, setLoading] = useState("");

  const [courseList, setCourseList] = useState([]);

  const navigate = useNavigate();
  useEffect(() => {
    getCourses();
  }, []);

  const getCourses = () => {
    axios
      .get("http://localhost:4200/course/all-courses", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        console.log(res.data.courses);
        setCourseList(res.data.courses);
        // navigate("/dashboard/courses");
      })
      .catch((err) => {
        console.log(err);
        toast.error("something went wrong...");
      });
  };

  // submitHandler used for submitting the form data w.r.t an event 'e'
  const submitHandler = (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append("image", image);
    axios
      .post("http://localhost:4200/course/add-course", formData, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        setLoading(false);
        console.log(res.data);
        toast.success("new course added...");
        navigate("/dashboard/courses");
      })
      .catch((err) => {
        setLoading(false);
        console.log(err);
        toast.error("something went wrong...");
      });
  };

  // fileHandler used for setting and uploading the image file w.r.t an event 'e'
  // set the image url to the value
  const fileHandler = (e) => {
    setImage(e.target.files[0]);
    setImageURL(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div>
      <form onSubmit={submitHandler} className="form">
        <h1>Add New Student</h1>
        <input placeholder="Student Name" />
        <input placeholder="Phone Number" />
        <input placeholder="Email" />
        <input placeholder="Full Address" />
        <select>
          <option>Select Course</option>
          {
            courseList.map((course)=>(
              <option >{course.courseName}</option>
            ))
          }
        </select>
        <input required onChange={fileHandler} type="file" />
        {imageURL && (
          <img className="your-logo" alt="your logo" src={imageURL} />
        )}
        <button type="submit" required className="submit-btn">
          {isLoading && <i className="fa-solid fa-spinner fa-spin-pulse"></i>}
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddStudents;
