import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const CourseDetails = () => {
  const params = useParams();
  const [course, setCourse] = useState({});
  const [studentList, setStudentList] = useState([]);

  useEffect(() => {
    getCourseDetail();
  }, []);

  const getCourseDetail = () => {
    axios
      .get(`http://localhost:4200/course/course-details/${params.id}`, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        console.log("Full API Response:", res.data);

        // If backend sends: { course: { course: {...}, studentList: [...] } }
        const courseData = res.data.course?.course || {};
        const students = res.data.course?.studentList || [];

        setCourse(courseData);
        setStudentList(students);

        // Log separately
        console.log("Course Details:", courseData);
        console.log("Student List:", students);
      })
      .catch((err) => {
        console.error(err);
        toast.error("Something went wrong...");
      });
  };

  return (
    <div>
        <div className="course-detail-wrapper">
            <img />

        </div>
    </div>
  )
}

export default CourseDetails;
