import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const AllCourses = () => {
  const [courseList, setCourseList] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getCourses();
  }, []);

  const getCourses = () => {
    axios
      .get("http://localhost:4200/course/all-courses", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        console.log(res.data.courses);
        setCourseList(res.data.courses);
      })
      .catch((err) => {
        console.log(err);
        toast.error("something went wrong...");
      });
  };
  return (
    <div className="course-wrapper">
      {courseList.map((course) => (
        <div onClick={()=>{navigate("dashboard/contact-detail/"+course._id)}}className="course-box" key={course._id}>
          <img alt="thumbnail" className="course-thumbnail" src={course.imageURL} />
          <h2 className="course-name">{course.courseName}</h2>
          <p className="course-price">Rs. {course.price} only</p>
        </div>
      ))}
    </div>
  );
};

export default AllCourses;
