import React, { useState } from "react";
import "../components/style.css";
import { Form } from "react-router-dom";
import axios from "axios";

// Signup function
const Signup = () => {
  // create bind
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [image, setImage] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [isLoading, setLoading] = useState(false)

  // prevent from default setting of autoloading the browser- manually refresh the browser
  const submitHandler = (event) => {
    event.preventDefault();
    // create and define the form data
    const formData = new FormData();
    formData.append("fullName", fullName);
    formData.append("email", email);
    formData.append("phone", phone);
    formData.append("password", password);
    formData.append("image", image);
    axios
      .post("http://localhost:4200/user/signup", formData)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // set the image url to the value
  const fileHandler = (e) => {
    setImage(e.target.files[0]);
    setImageUrl(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div className="signup-wrapper">
      <div className="signup-box">
        <div className="signup-left">
          <img alt="book logo" src={require("../assets/book_logo2.png")} />
          <h1 className="signup-left-heading">Institute Management App</h1>
          <p className="signup-left-para">
            <b>Manage Institute Data in an easier way...</b>
          </p>
        </div>

        <div className="signup-right">
          <Form onSubmit={submitHandler} className="signup-form">
            <h1>Create Your Account</h1>
            <input required onChange={(e) => {setFullName(e.target.value);}} type="text" placeholder="Institute FUll Name"/>
            <input required onChange={(e) => {setEmail(e.target.value);}} type="email" placeholder="Email"/>
            <input required onChange={(e) => {setPhone(e.target.value);}} type="text" placeholder="Phone"/>
            <input required onChange={(e) => { setPassword(e.target.value);}} type="password" placeholder="Password"/>
            <input required onChange={fileHandler} type="file" />
            {imageUrl && <img className="your-logo" alt="your logo" src={imageUrl} />}
            <button type="submit"><i> <i class="fa-solid fa-spinner fa-spin-pulse"></i></i>submit</button>
          </Form>
        </div>
      </div>
    </div>
  );
};

export default Signup;
