import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const AddStudents = () => {
  const [fullName, setStudentName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [address, setFullAddress] = useState("");
  const [image, setImage] = useState("null");

  const [imageURL, setImageURL] = useState("");
  const [isLoading, setLoading] = useState("");

  const navigate = useNavigate();

  const [courseList, setCourseList] = useState([]);
  
    useEffect(() => {
      getCourses();
    }, []);
  
  const getCourses = () => {
    axios
      .get("http://localhost:4200/course/all-courses", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        console.log(res.data.courses);
        setCourseList(res.data.courses);
      })
      .catch((err) => {
        console.log(err);
        toast.error("something went wrong...");
      });
  };

  // create and define the form data
  const formData = new FormData();
  formData.append("Student Name", fullName);
  formData.append("phone", phone);
  formData.append("email", email);
  formData.append("address", address);
  formData.append("image", image);

  const submitHandler = (e) => {
    e.preventDefault();
    setLoading(true);
    axios
      .post("http://localhost:4200/course/add-course", formData, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        setLoading(false);
        console.log(res.data);
        toast.success("new student added...");
        navigate("/dashboard/student");
      })
      .catch((err) => {
        setLoading(false);
        console.log(err);
        toast.error("something went wrong...");
      });
  };

  // set the image url to the value
  const fileHandler = (e) => {
    setImage(e.target.files[0]);
    setImageURL(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div>
      <form onSubmit={submitHandler} className="form">
        <h1>Add New Student</h1>
        <input
          required
          onChange={(e) => {
            setStudentName(e.target.value);
          }}
          placeholder="Student Name"
          type="text"
        />
        <input
          required
          onChange={(e) => {
            setPhone(e.target.value);
          }}
          placeholder="Phone Number"
          type="number"
        />
        <input
          required
          onChange={(e) => {
            setEmail(e.target.value);
          }}
          placeholder="Email"
          type="text"
        />
        <input
          required
          onChange={(e) => {
            setFullAddress(e.target.value);
          }}
          placeholder="Full Address"
          type="text"
        />
        <select>
          <option>Select Course</option>
          {
            courseList.map((course)=>(
              <option value={course}>{course.courseName}</option>
            ))
          }
        </select>
        <input required onChange={fileHandler} type="file" />
        {imageURL && (
          <img className="your-logo" alt="your logo" src={imageURL} />
        )}
        <button type="submit" className="submit-btn">
          {isLoading && <i className="fa-solid fa-spinner fa-spin-pulse"></i>}
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddStudents;
