import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useLocation, useNavigate } from "react-router-dom";

const AddCourses = () => {
  const [courseName, setCourseName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [image, setImage] = useState("null");

  const [imageURL, setImageURL] = useState("");
  const [isLoading, setLoading] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();

  // When page loads, check if editing an existing course
  // useEffect(() => {
  //   if (location.state && location.state.course) {
  //     const c = location.state.course;
  //     console.log("Editing course:", c); // ✅ Logs clicked course info

  //     // Pre-fill form fields
  //     setCourseName(c.courseName || "");
  //     setDescription(c.description || "");
  //     setPrice(c.price || "");
  //     setStartDate(c.startDate ? c.startDate.split("T")[0] : "");
  //     setEndDate(c.endDate ? c.endDate.split("T")[0] : "");
  //     setImageURL(c.imageURL || "");
  //   }
  // }, [location.state]);

  useEffect(() => {
    if (location.state) {
      console.log(location.state.course);
    }
  }, []);

  const submitHandler = (e) => {
    e.preventDefault();
    setLoading(true);

    // create and define the form data
    const formData = new FormData();
    formData.append("courseName", courseName);
    formData.append("description", description);
    formData.append("price", price);
    formData.append("startDate", startDate);
    formData.append("endDate", endDate);
    formData.append("image", image);

    axios
      .post("http://localhost:4200/course/add-course", formData, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        setLoading(false);
        console.log(res.data);
        toast.success("new course added...");
        navigate("/dashboard/courses");
      })
      .catch((err) => {
        setLoading(false);
        console.log(err);
        toast.error("something went wrong...");
      });
  };

  // set the image url to the value
  const fileHandler = (e) => {
    setImage(e.target.files[0]);
    setImageURL(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div>
      <form onSubmit={submitHandler} className="form">
        <h1></h1>
        <input
          required
          onChange={(e) => {
            setCourseName(e.target.value);
          }}
          placeholder="Course Name"
          type="text"
        />
        <input
          required
          onChange={(e) => {
            setDescription(e.target.value);
          }}
          placeholder="Description"
          type="text"
        />
        <input
          required
          onChange={(e) => {
            setPrice(e.target.value);
          }}
          placeholder="Price"
          type="number"
        />
        <input
          required
          onChange={(e) => {
            setStartDate(e.target.value);
          }}
          placeholder="Starting Date (DD-MM-YY)"
          type="date"
        />
        <input
          required
          onChange={(e) => {
            setEndDate(e.target.value);
          }}
          placeholder="End Date (DD-MM-YY)"
          type="date"
        />
        <input required onChange={fileHandler} type="file" />
        {imageURL && <img className="your-logo" alt="Preview" src={imageURL} />}
        <button type="submit" className="submit-btn">
          {isLoading && <i className="fa-solid fa-spinner fa-spin-pulse"></i>}
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddCourses;
