import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useLocation, useNavigate } from "react-router-dom";

const AddCourses = () => {
  const [courseName, setCourseName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [image, setImage] = useState(null);

  const [imageURL, setImageURL] = useState("");
  const [isLoading, setLoading] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();

  // Pre-fill form for edit mode
  useEffect(() => {
    if (location.state?.course) {
      const c = location.state.course;
      setCourseName(c.courseName || "");
      setDescription(c.description || "");
      setPrice(c.price || "");
      setStartDate(c.startDate || "");
      setEndDate(c.endDate || "");
      setImageURL(c.imageURL || "");
    }
  }, [location.state]);

  const submitHandler = (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append("courseName", courseName);
    formData.append("price", price);
    formData.append("description", description);
    formData.append("startDate", startDate);
    formData.append("endDate", endDate);

    if (image) {
      // ✅ Only append if a new file was chosen
      formData.append("image", image);
    }
    
    if (location.state?.course) {
      // UPDATE course
      axios
        .put(
          `http://localhost:4200/course/${location.state.course._id}`,
          formData,
          {
            headers: {
              Authorization: "Bearer " + localStorage.getItem("token"),
            },
          }
        )
        .then((res) => {
          setLoading(false);
          toast.success("Course updated successfully");
          navigate(`/dashboard/course-details/${location.state.course._id}`);
        })
        .catch((err) => {
          setLoading(false);
          console.error(err);
          toast.error("Something went wrong while updating...");
        });
    } else {
      // ADD new course
      axios
        .post("http://localhost:4200/course/add-course", formData, {
          headers: {
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
        })
        .then((res) => {
          setLoading(false);
          toast.success("New course added successfully");
          navigate("/dashboard/courses");
        })
        .catch((err) => {
          setLoading(false);
          console.error(err);
          toast.error("Something went wrong while adding...");
        });
    }
  };

  const fileHandler = (e) => {
    setImage(e.target.files[0]);
    setImageURL(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div>
      <form onSubmit={submitHandler} className="form">
        <h1>{location.state ? "Edit Course" : "Add New Course"}</h1>
        <input
          value={courseName}
          required
          onChange={(e) => setCourseName(e.target.value)}
          placeholder="Course Name"
          type="text"
        />
        <input
          value={description}
          required
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          type="text"
        />
        <input
          value={price}
          required
          onChange={(e) => setPrice(e.target.value)}
          placeholder="Price"
          type="number"
        />
        <input
          value={startDate}
          required
          onChange={(e) => setStartDate(e.target.value)}
          placeholder="Starting Date (DD-MM-YY)"
          type="text"
        />
        <input
          value={endDate}
          required
          onChange={(e) => setEndDate(e.target.value)}
          placeholder="End Date (DD-MM-YY)"
          type="text"
        />
        <input required={!location.state} onChange={fileHandler} type="file" />
        {imageURL && <img className="your-logo" alt="Preview" src={imageURL} />}
        <button type="submit" className="submit-btn">
          {isLoading && <i className="fa-solid fa-spinner fa-spin-pulse"></i>}
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddCourses;
