import React, { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useLocation, useNavigate } from "react-router-dom";

const AddCourses = () => {
  const [courseName, setCourseName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [image, setImage] = useState(null);

  const [imageURL, setImageURL] = useState("");
  const [isLoading, setLoading] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    if (location.state) {
      setCourseName(location.state.course.courseName);
      setDescription(location.state.course.description);
      setPrice(location.state.course.price);
      setStartDate(location.state.course.startDate);
      setEndDate(location.state.course.endDate);
      setImageURL(location.state.course.imageURL);
    }
  }, [location.state]);

  const submitHandler = (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append("courseName", courseName);
    formData.append("description", description);
    formData.append("price", price);
    formData.append("startDate", startDate);
    formData.append("endDate", endDate);
    if (image) {
      formData.append("image", image);
    }

    if (location.state && location.state.course) {
      // Update course
      axios
        .put(
          `http://localhost:4200/course/update-course/${location.state.course._id}`,
          formData,
          {
            headers: {
              Authorization: "Bearer " + localStorage.getItem("token"),
            },
          }
        )
        .then((res) => {
          setLoading(false);
          toast.success("Course updated...");
          navigate(`/dashboard/course-details/${location.state.course._id}`);
        })
        .catch(() => {
          setLoading(false);
          toast.error("Something went wrong...");
        });
    } else {
      // Add course
      axios
        .post("http://localhost:4200/course/add-course", formData, {
          headers: {
            Authorization: "Bearer " + localStorage.getItem("token"),
          },
        })
        .then(() => {
          setLoading(false);
          toast.success("New course added...");
          navigate("/dashboard/courses");
        })
        .catch(() => {
          setLoading(false);
          toast.error("Something went wrong...");
        });
    }
  };

  const fileHandler = (e) => {
    setImage(e.target.files[0]);
    setImageURL(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div>
      <form onSubmit={submitHandler}>
        <h1>{location.state ? "Edit Course" : "Add New Course"}</h1>

        <input
          value={courseName}
          required
          onChange={(e) => setCourseName(e.target.value)}
          placeholder="Course Name"
          type="text"
        />

        {/* Changed input to textarea for multiline description */}
        <textarea
          value={description}
          required
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          rows="4"
        />

        <input
          value={price}
          required
          onChange={(e) => setPrice(e.target.value)}
          placeholder="Price"
          type="number"
        />

        <input
          value={startDate}
          required
          onChange={(e) => setStartDate(e.target.value)}
          placeholder="Starting Date (DD-MM-YY)"
          type="text"
        />

        <input
          value={endDate}
          required
          onChange={(e) => setEndDate(e.target.value)}
          placeholder="End Date (DD-MM-YY)"
          type="text"
        />

        <input
          required={!location.state}
          onChange={fileHandler}
          type="file"
        />

        {imageURL && <img alt="Preview" src={imageURL} />}

        <button type="submit">
          {isLoading && <i className="fa-solid fa-spinner fa-spin-pulse"></i>}
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddCourses;
