import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const CourseDetails = () => {
  const params = useParams();
  const [course, setCourse] = useState(null); // store single course

  useEffect(() => {
    console.log(params.id);
    getCourseDetail();
  }, []);

  const getCourseDetail = () => {
    axios
      .get(`http://localhost:4200/course/course-details/params.id}`, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        console.log(res.data.course); // make sure backend returns "course"
        setCourse(res.data.course); // store single course
      })
      .catch((err) => {
        console.log(err);
        toast.error("Something went wrong...");
      });
  };

  return (
    <div>
      {course ? (
        <>
          <img src={course.imageURL} alt={course.courseName} />
          <h2>{course.courseName}</h2>
          <p>Price: Rs. {course.price}</p>
          <p>{course.description}</p>
        </>
      ) : (
        <p>Loading course details...</p>
      )}
    </div>
  );
};

export default CourseDetails;
