import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const CourseDetails = () => {
  const params = useParams();
  const [course, setCourse] = useState({});
  const [studentList, setStudentList] = useState([]);

  useEffect(() => {
    console.log(params.id);
    getCourseDetail(); // Call the function
  }, []);

  const getCourseDetail = () => {
    axios
      .get("http://localhost:4200/course/course-details/" + params.id, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        console.log(res.data); // This should contain your students list
        setCourse(res.data.course.course);
        setStudentList(res.data.course.studentList)
      })
      .catch((err) => {
        console.log(err);
        toast.error("Something went wrong...");
      });
  };

  return <div>CourseDetails</div>;
};

export default CourseDetails;
