import axios from "axios";
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const AddStudents = () => {
  const [fullName, setFullName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [courseId, setCourseId] = useState("");
  const [image, setImage] = useState(null);

  const [imageURL, setImageURL] = useState("");
  const [isLoading, setLoading] = useState("");

  const [courseList, setCourseList] = useState([]);
  const location = useLocation();

  const navigate = useNavigate();

  useEffect(() => {
    getCourses();
    if (location.state) {
      setFullName(location.state.student.fullName);
      setPhone(location.state.student.phone);
      setEmail(location.state.student.email);
      setAddress(location.state.student.address);
      setCourseId(location.state.student.courseId);
      setImageURL(location.state.student.imageURL);
    } else {
      setFullName("");
      setPhone("");
      setEmail("");
      setAddress("");
      setCourseId("");
      setImageURL("");
    }
  }, [location]);

  const getCourses = () => {
    axios
      .get("http://localhost:4200/course/all-courses", {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        console.log(res.data.courses);
        setCourseList(res.data.courses);
        // navigate("/dashboard/courses");
      })
      .catch((err) => {
        console.log(err);
        toast.error("something went wrong...");
      });
  };

  // submitHandler used for submitting the form data w.r.t an event 'e'
  const submitHandler = (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append("fullName", fullName);
    formData.append("phone", phone);
    formData.append("email", email);
    formData.append("address", address);
    formData.append("courseId", courseId);
    if (image) {
      formData.append("image", image);
    }

    if(location.state)
    {
      axios
      .put("http://localhost:4200/student/"+location.state.student._id, formData, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        setLoading(false);
        console.log(res.data);
        toast.success("student detail updated...");
        navigate("/dashboard/student-details/"+location);
      })
      .catch((err) => {
        setLoading(false);
        console.log(err);
        toast.error("something went wrong...");
      });
    }
    axios
      .post("http://localhost:4200/student/add-student", formData, {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
      .then((res) => {
        setLoading(false);
        console.log(res.data);
        toast.success("new student added...");
        navigate("/dashboard/courses");
      })
      .catch((err) => {
        setLoading(false);
        console.log(err);
        toast.error("something went wrong...");
      });
  };

  // fileHandler used for setting and uploading the image file w.r.t an event 'e'
  // set the image url to the value
  const fileHandler = (e) => {
    setImage(e.target.files[0]);
    setImageURL(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div>
      <form onSubmit={submitHandler} className="form">
        <h1>{location.state ? "Edit Student Detail" : "Add New Student"}</h1>
        <input
          value={fullName}
          onChange={(e) => {
            setFullName(e.target.value);
          }}
          placeholder="Student Name"
        />
        <input
          value={phone}
          onChange={(e) => {
            setPhone(e.target.value);
          }}
          placeholder="Phone Number"
        />
        <input
          value={email}
          onChange={(e) => {
            setEmail(e.target.value);
          }}
          placeholder="Email"
        />

        <input
          value={address}
          onChange={(e) => {
            setAddress(e.target.value);
          }}
          placeholder="Full Address"
        />
        <select
          disabled={location.state}
          value={courseId}
          onChange={(e) => {
            setCourseId(e.target.value);
          }}
        >
          <option>Select Course</option>
          {courseList.map((course) => (
            <option key={course._id} value={course._id}>
              {course.courseName}
            </option>
          ))}
        </select>
        <input required onChange={fileHandler} type="file" />
        {imageURL && (
          <img className="student-pic" alt="student pic" src={imageURL} />
        )}
        <button type="submit" required className="submit-btn">
          {isLoading && <i className="fa-solid fa-spinner fa-spin-pulse"></i>}
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddStudents;
