import React, { useState } from "react";

const AddCourses = () => {
  const [courseName, setCourseName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [startDate, setStartDate] = useState("");
  const [endDate, setendDate] = useState("");
  const [image, setImage] = useState(null);

  const [imageURL, setImageURL] = useState("");
  const [isLoading, setLoading] = useState("");

  return (
    <div>
      <form className="form">
        <input onChange={e=>{setCourseName(e.target.value)}} placeholder="Course Name" type="text" />
        <input onChange={e=>{setDescription(e.target.value)}} placeholder="Description" type="text" />
        <input onChange={e=>{setPrice(e.target.value)}} placeholder="Price" type="number" />
        <input onChange={e=>{setCourseName(e.target.value)}} placeholder="Start Date (DD-MM-YY)" type="text" />
        <input onChange={e=>{setCourseName(e.target.value)}} placeholder="End Date (DD-MM-YY)" type="text" />
        <input type="file" />
        <button className="submit-btn">Submit</button>
      </form>
    </div>
  );
};

export default AddCourses;
