import axios from "axios";
import React, { useState } from "react";

const AddCourses = () => {
  const [courseName, setCourseName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [image, setImage] = useState(null);

  const [imageURL, setImageURL] = useState("");
  const [isLoading, setLoading] = useState("");

  // submitHandler used for submitting the form data w.r.t an event 'e'
  const submitHandler = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("courseName", courseName);
    formData.append("description", description);
    formData.append;
    axios.post("http://localhost:4200/course/add-course", formData, {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("token"),
      },
    })
    .then(res=>{
      console.log(res.data);
      toast.success("new course added...")
    })
    .catch(err=>{
      console.log(er)
    })
  };

  // fileHandler used for setting and uploading the image file w.r.t an event 'e'
  // set the image url to the value
  const fileHandler = (e) => {
    setImage(e.target.files[0]);
    setImageURL(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div>
      <form onSubmit={submitHandler} className="form">
        <input
          required
          onChange={(e) => {
            setCourseName(e.target.value);
          }}
          placeholder="Course Name"
          type="text"
        />
        <input
          required
          onChange={(e) => {
            setDescription(e.target.value);
          }}
          placeholder="Description"
          type="text"
        />
        <input
          required
          onChange={(e) => {
            setPrice(e.target.value);
          }}
          placeholder="Price"
          type="number"
        />
        <input
          required
          onChange={(e) => {
            setStartDate(e.target.value);
          }}
          placeholder="Start Date (DD-MM-YY)"
          type="text"
        />
        <input
          required
          onChange={(e) => {
            setEndDate(e.target.value);
          }}
          placeholder="End Date (DD-MM-YY)"
          type="text"
        />
        <input required onChange={fileHandler} type="file" />
        {imageURL && (
          <img className="your-logo" alt="your logo" src={imageURL} />
        )}
        <button type="submit" required className="submit-btn">
          Submit
        </button>
      </form>
    </div>
  );
};

export default AddCourses;
