import React, { useState } from "react";
import "../components/style.css";
import { Form, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
//import "react-toastify/dist/ReactTOastify.css";


// Signup function
const Login = () => {
  // create bind
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setLoading] = useState(true);

  // getting from react-router-dom
  const navigate = useNavigate();

  // prevent from default setting of autoloading the browser- manually refresh the browser
  const submitHandler = (event) => {
    event.preventDefault();
    setLoading(true);

    axios
      .post("http://localhost:4200/user/login", {
        email: email,
        password: password,
      })
      .then((res) => {
        setLoading(false);
        localStorage.setItem("token", res.data.token)
        localStorage.setItem("token", res.data.fullName)
        localStorage.setItem("token", res.data.token)
        //toast.success("Welcome to your Institute!!");
        navigate("/dashboard");
        console.log(res.data);
      })
      .catch((err) => {
        setLoading(false);
        toast.error("Something went wrong!");
        console.log(err);
      });
  };

  return (
    <div className="signup-wrapper">
      <div className="signup-box">
        <div className="signup-left">
          <img alt="book logo" src={require("../assets/book_logo2.png")} />
          <h1 className="signup-left-heading">Institute Management App</h1>
          <p className="signup-left-para">
            Manage Institute Data in an easier way...
          </p>
        </div>

        <div className="signup-right">
          <Form onSubmit={submitHandler} className="signup-form">
            <h1>Login with Your Account</h1>

            <input
              required
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              type="email"
              placeholder="Email"
            />

            <input
              required
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              type="password"
              placeholder="Password"
            />

            <button type="submit">
              {isLoading && <i class="fa-solid fa-spinner fa-spin-pulse"></i>}
              submit
            </button>

            <Link className="link" to="/signup">Create Your Account</Link>
          </Form>
        </div>
      </div>
    </div>
  );
};

export default Login;
